INSERT INTO `produtos` (`id`, `nome`, `descricao`, `preco`, `imagem`, `categoria`, `subcategoria`, `destaque`, `mais_vendido`, `imagem_2`, `imagem_3`, `imagem_4`, `indisponivel`) VALUES
(14, 'Roupeiro Escócia 6 portas', 'Roupeiro Escócia com 6 portas e 2 gavetas, ideal para manter suas roupas e acessórios organizados com praticidade e elegância. Conta com amplo espaço interno, cabideiros, prateleiras e gavetas funcionais. Possui pés que facilitam a limpeza e está disponível em diversas cores modernas: preto, ipê texturizado, ipê texturizado com off white e branco. Com design contemporâneo e acabamento de qualidade, é uma excelente opção para otimizar seu quarto. Medidas: Altura 1,82m, Largura 1,57m, Profundidade 0,47m. Espelho opcional.', 749.00, '/img/product_images/roupeiro_escocia_6_portas.jpg', 'quarto', 'guarda-roupas', 1, 1, '', '', '', 0),
(16, 'Roupeiro MS912', 'Roupeiro MS912 com design moderno e excelente aproveitamento de espaço, ideal para quem busca organização e estilo no quarto. Possui 6 portas e 2 gavetas, além de sapateira interna e pés que facilitam a limpeza. Conta com cabideiros e prateleiras bem distribuídas, oferecendo amplo espaço para roupas, calçados e acessórios. Disponível nas cores: castanho, naturale/branco e freijó/cinza. Medidas: Altura 2,04m, Largura 1,65m, Profundidade 0,47m. Espelho opcional.', 969.00, '/img/product_images/roupeiro_ms912.jpg', 'quarto', 'guarda-roupas', 1, 1, NULL, NULL, NULL, 0),
(17, 'Roupeiro Capri', 'Roupeiro Capri modelo capelinha, com design moderno e funcional, ideal para manter o quarto organizado com estilo. Conta com 4 portas, 3 gavetas espaçosas, pés elevados para facilitar a limpeza e espelho central. Internamente, possui nichos e cabideiro para melhor distribuição de roupas e acessórios. Disponível nas cores branco flex rosa, branco, cast/baunilha, castanho e freijó/cinza. Medidas: Altura 2,04m, Largura 1,65m, Profundidade 0,47m. Sistema Flex Color incluso.', 999.00, '/img/product_images/roupeiro_capri.jpg', 'quarto', 'guarda-roupas', 1, 1, NULL, NULL, NULL, 0),
(18, 'Roupeiro Avai', 'Roupeiro Avai, com design elegante e estrutura reforçada, é ideal para quem busca praticidade e amplo espaço interno. Possui 6 portas, 4 gavetas centrais com corrediças metálicas, pés elevados que facilitam a limpeza e opção de espelho. Seu interior conta com nichos bem distribuídos e cabideiros, proporcionando organização eficiente para roupas e acessórios. Disponível na cor freijó e freijó/off white. Medidas: Altura 2,04m, Largura 1,77m, Profundidade 0,47m.', 989.00, '/img/product_images/roupeiro_avai_1.jpg', 'quarto', 'guarda-roupas', 1, 1, '/img/product_images/roupeiro_avai_2.jpg', NULL, NULL, 0),
(19, 'Roupeiro Mafra', 'Roupeiro Mafra, com estilo contemporâneo e excelente aproveitamento interno, é a escolha ideal para quem busca funcionalidade e elegância no quarto. Conta com 6 portas, 2 gavetas centrais com corrediças metálicas, sapateira interna e pés elevados que facilitam a limpeza. Seu interior bem planejado oferece nichos, cabideiros e espaço otimizado para roupas, calçados e acessórios. Disponível na cor freijó/off white e freijó/cinza. Medidas: Altura 2,04m, Largura 1,77m, Profundidade 0,47m. Espelho opcional.', 969.00, '/img/product_images/roupeiro_mafra_1.jpg', 'quarto', 'guarda-roupas', 1, 1, '/img/product_images/roupeiro_mafra_2.jpg', NULL, NULL, 0),
(20, 'Roupeiro Bolívia', 'Roupeiro Bolívia, com design moderno e sofisticado, ideal para ambientes compactos sem abrir mão da organização. Possui 3 portas de correr, sendo uma com espelho central, e 2 gavetas internas com excelente espaço. Seu interior conta com prateleiras, nichos e cabideiro para melhor disposição das roupas e acessórios. Acabamento na cor freijó/cinza e freijó/off white, unindo elegância e funcionalidade. Medidas: Altura 2,04m, Largura 1,64m, Profundidade 0,53m.', 1099.00, '/img/product_images/roupeiro_bolivia_1.jpg', 'quarto', 'guarda-roupas', 1, 1, '/img/product_images/roupeiro_bolivia_2.jpg', NULL, NULL, 0),
(21, 'Rack Istambul', 'Rack Istambul, com design elegante e contemporâneo, ideal para compor salas modernas. Suporta TVs de até 70 polegadas e possui 2 portas com detalhe arredondado que conferem um toque sofisticado ao móvel. Conta com nichos abertos para aparelhos eletrônicos e decoração. Estrutura robusta com pés de madeira e acabamento na cor nature/off white. Medidas: Altura 0,62m, Largura 2,00m, Profundidade 0,36m.\r\n\r\n', 799.00, '/img/product_images/rack_istambul.jpg', 'sala_de_estar', 'racks', 1, 1, NULL, NULL, NULL, 0),
(22, 'Rack Aurum', 'Rack Aurum, com estilo retrô e acabamento elegante em nature/off white. Ideal para salas modernas e aconchegantes, possui 2 portas de correr com detalhe frisado, proporcionando charme e praticidade. Suporta TVs de até 65 polegadas e conta com nichos para decoração ou aparelhos eletrônicos. Estrutura firme com pés inclinados em madeira. Medidas: Altura 0,66m, Largura 1,60m, Profundidade 0,36m.\n\n', 449.00, '/img/product_images/rack_aurum.jpg', 'sala_de_estar', 'racks', 1, 1, '', '', '', 1),
(23, 'Roupeiro Alasca', 'Roupeiro Alasca, imponente e funcional, combina o acabamento freijó/off white com espelhos centrais, que ampliam o ambiente e valorizam a decoração. Possui portas com detalhes frisados e puxadores embutidos, além de corrediças telescópicas que garantem suavidade no uso. Internamente, conta com excelente divisão: cabideiros, nichos, gavetas e espaço para calçados. Medidas: Altura 2,22m, Largura 2,16m, Profundidade 0,47m.', 1899.00, '/img/product_images/roupeiro_alasca.jpg', 'quarto', 'guarda-roupas', 1, 1, NULL, NULL, NULL, 0),
(24, 'Roupeiro Rizon', 'Roupeiro Rizon, com design imponente e moderno, é fabricado 100% em MDF e disponível nas cores Cumaru ou Cumaru/Fendi. Seus puxadores com acabamento amadeirado, colunado em 25mm, conferem elegância e robustez. Internamente, possui excelente divisão com espaço \"ele e ela\", corrediças telescópicas, gavetas internas e amplo espaço para roupas e acessórios. Cor interna igual à externa. Acompanha kit pés.\r\nMedidas: Altura 2,43m, Largura 2,44m, Profundidade 0,57m.', 2699.00, '/img/product_images/roupeiro_rizon_1.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(25, 'Roupeiro Diamond Plus c/ 1 Espelho', 'Roupeiro Diamond Plus com 1 Espelho, sofisticado e funcional, é fabricado 100% em MDF com portas de correr, sendo uma com espelho de segurança com película protetora. Possui trilho inferior em alumínio e rodanas com amortecedor, garantindo deslizamento suave. A porta FLEX permite variar a combinação visual (Fendi/Cumaru). Internamente, conta com divisões amplas, corrediças telescópicas e acabamento interno na mesma cor externa. Disponível nas cores Neve ou Cumaru/Fendi/Cumaru. Acompanha kit pés.\r\nMedidas: Altura 2,43m, Largura 2,41m, Profundidade 0,55m.', 2899.00, '/img/product_images/roupeiro_diamond_plus.jpg', 'quarto', 'guarda-roupas', 1, 0, NULL, NULL, NULL, 0),
(26, 'Roupeiro Sollo Plus c/ 1 Espelho', 'Roupeiro Sollo Plus com 1 Espelho combina elegância e praticidade. Fabricado 100% em MDF, conta com portas de correr, sendo uma com espelho de segurança com película protetora. As portas deslizam suavemente graças ao trilho inferior em alumínio e rodanas com amortecedor. A porta FLEX (nas cores Cumaru/Fendi) permite personalização. Internamente, possui divisórias bem distribuídas, gavetas com corrediças telescópicas e acabamento interno igual ao externo. Disponível nas cores Neve ou Cumaru/Fendi/Cumaru. Acompanha kit pés.\r\nMedidas: Altura 2,43m, Largura 2,736m, Profundidade 0,555m.', 3099.00, '/img/product_images/roupeiro_sollo_plus.jpg', 'quarto', 'guarda-roupas', 1, 1, NULL, NULL, NULL, 0),
(27, 'Roupeiro Malta', 'Roupeiro Malta é ideal para quem busca um móvel compacto, funcional e com design moderno. Com 6 portas e 2 gavetas, oferece excelente organização para roupas, acessórios e calçados. As portas possuem detalhes frisados que conferem um toque elegante. Fabricado na cor Freijó/Off White, conta com puxadores em madeira e pés inclusos, garantindo maior estabilidade e charme. Seu interior é bem dividido, com cabideiros, nichos e prateleiras, otimizando o espaço disponível.\r\nMedidas: Altura 2,04m, Largura 1,65m, Profundidade 0,47m.', 949.00, '/img/product_images/roupeiro_malta.jpg', 'quarto', 'guarda-roupas', 1, 1, NULL, NULL, NULL, 0),
(28, 'Roupeiro Virtus', 'Roupeiro Virtus é ideal para quem busca um móvel robusto, elegante e funcional. Produzido 100% em MDF, conta com portas de correr com espelho com película de segurança colado em painel de 25mm. Possui trilho inferior em alumínio e roldanas com amortecedor, garantindo abertura suave. A porta FLEX permite abertura em ambos os sentidos. Internamente, o roupeiro oferece excelente aproveitamento com nichos, cabideiros e gavetas com corrediças telescópicas.\r\nDisponível nas cores Neve e Cumaru/Fendi/Cumaru.\r\nMedidas: Altura 2,43m, Largura 1,62m, Profundidade 0,55m.', 2199.00, '/img/product_images/roupeiro_virtus.jpg', 'quarto', 'guarda-roupas', 1, 1, NULL, NULL, NULL, 0),
(29, 'Roupeiro Robust', 'Roupeiro Robust impressiona pelo tamanho e funcionalidade. Fabricado 100% em MDF, possui puxadores com acabamento amadeirado e portas coladas em painéis de 25mm. Seu interior no modelo “ele e ela” oferece amplo espaço, com nichos superiores, cabideiros, e gavetas com corrediças telescópicas. A cor interna acompanha o acabamento externo.\r\nDisponível nas cores Cumaru/Fendi e Cumaru.\r\nMedidas: Altura 2,43m, Largura 2,74m, Profundidade 0,57m.', 2899.00, '/img/product_images/roupeiro_robust.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(30, 'Roupeiro Vitória', 'Roupeiro Vitória é compacto e funcional, ideal para quem busca praticidade sem abrir mão da elegância. Produzido em MDP/MDF, conta com corrediças metálicas, cabideiro de madeira e espelho central incluso. O modelo possui gavetas espaçosas, portas bem distribuídas e está disponível nas cores Acacia/Reflecta, Canelato/Off e Branco.\r\nMedidas: Altura 1,80m, Largura 1,48m, Profundidade 0,40m.', 899.00, '/img/product_images/roupeiro_vitoria.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(31, 'Roupeiro Munique', 'Roupeiro Munique oferece amplo espaço interno com design moderno e sofisticado. Produzido em MDP de 15mm, possui gavetas com corrediças metálicas, cabideiros de madeira, puxadores em ABS e dobradiças metálicas, garantindo durabilidade e praticidade. O modelo conta com 8 portas bem distribuídas e está disponível nas cores Branco, Amêndoa Touch/Off e Amêndoa Touch.\r\nMedidas: Altura 2,01m, Largura 1,62m, Profundidade 0,47m.', 1099.00, '/img/product_images/roupeiro_munique.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(32, 'Roupeiro Cádis', 'Roupeiro Cádis é uma peça elegante e funcional, perfeita para otimizar o espaço de forma sofisticada. Desenvolvido em MDP com pintura UV, apresenta cabideiros e calceiros metálicos, conferindo resistência e durabilidade. O modelo conta com gavetas externas equipadas com corrediças metálicas, puxadores centrais de alumínio e puxadores em PVC, garantindo praticidade no uso diário. Inclui um kit de pés e espelho, acrescentando um toque moderno ao ambiente. Está disponível nas opções de cores Branco, Amêndola/Off e Amêndola Touch.\r\nMedidas: Altura 2,17m, Largura 2,00m, Profundidade 0,47m.', 1399.00, '/img/product_images/roupeiro_cadis.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(33, 'Roupeiro New Realce', 'Roupeiro New Realece é a escolha ideal para quem busca estilo e funcionalidade em um só móvel. Fabricado em MDP com pintura UV, possui cabideiros metálicos que garantem resistência e durabilidade. Este modelo apresenta gavetas externas com corrediças metálicas, proporcionando organização e praticidade. Os puxadores em PVC adicionam um toque moderno e elegante. O roupeiro vem com um kit de pés, elevando seu design e facilitando a limpeza do ambiente. Está disponível nas cores Branco, Amêndola/Off e Amêndola Touch.\r\nMedidas: Altura 2,17m, Largura 2,01m, Profundidade 0,47m.', 1399.00, '/img/product_images/roupeiro_new_realce.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(34, 'Roupeiro Morretes 3.9', 'Roupeiro Morretes 3.9 é a combinação perfeita de estilo e funcionalidade, ideal para quem valoriza organização e design. Composto por três portas de correr, este modelo garante um uso eficiente do espaço. Os puxadores em MDF revestidos e as nove gavetas externas com corrediças telescópicas oferecem praticidade e sofisticação. Conta também com dois cabideiros/calceiros em alumínio e portas de correr que utilizam trilho metálico, assegurando durabilidade e facilidade de uso. Inclui um kit de pés e a opção de espelho, que pode ser adquirido à parte. As opções de cores disponíveis são Jatobá, Jatobá/Grafite e Jatobá/Areia.\r\nMedidas: Altura 2,14m, Largura 1,94m, Profundidade 0,49m.', 1599.00, '/img/product_images/roupeiro_morretes_39.jpg', 'quarto', 'guarda-roupas', 1, 1, NULL, NULL, NULL, 0),
(35, 'Roupeiro Guarapuava 6.4', 'Roupeiro Guarapuava 6.4 é a solução ideal para quem busca organização e estilo em um só móvel. Composto por seis portas, este modelo apresenta puxadores em MDF revestidos, que adicionam um toque de elegância. Conta ainda com quatro gavetas externas equipadas com corrediças telescópicas, permitindo um acesso fácil e prático. Os quatro cabideiros/calceiros em alumínio garantem a resistência e a funcionalidade do produto. O roupeiro inclui um kit de pés, proporcionando um acabamento moderno. As opções de cores disponíveis são Jatobá, Jatobá/Grafite e Jatobá/Areia.\r\nMedidas: Altura 2,14m, Largura 1,94m, Profundidade 0,46m.', 1349.00, '/img/product_images/roupeiro_guarapuava_64.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(36, 'Roupeiro Lapa', 'Roupeiro Lapa é uma peça que une funcionalidade e design moderno. Com duas portas de correr, este modelo é ideal para otimizar espaços menores. Fabricado em MDF/MDP, possui puxadores em MDF revestidos que conferem um acabamento sofisticado. Conta com duas gavetas internas com corrediças telescópicas e dois cabideiros/calceiros em alumínio, garantindo praticidade e resistência. As portas de correr são equipadas com trilho metálico e perfil anti-empeno, proporcionando durabilidade e facilidade no movimento. Inclui um kit de pés. Está disponível nas cores Jatobá, Jatobá/Grafite e Jatobá/Areia.\r\nMedidas: Altura 2,14m, Largura 1,40m, Profundidade 0,49m.', 1069.00, '/img/product_images/roupeiro_lapa.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(37, 'Roupeiro Maringá', 'Roupeiro Maringá é a escolha perfeita para quem busca estilo e funcionalidade, com três portas de correr que otimizam o espaço. Fabricado em MDF, apresenta puxadores em MDF revestidos, que proporcionam um acabamento sofisticado. Este modelo conta com duas gavetas internas com corrediças telescópicas para um acesso prático e eficiente. Possui, ainda, três cabideiros/calceiros em alumínio, assegurando durabilidade e resistência. As portas de correr utilizam trilhos metálicos e perfil anti-empeno, garantindo o movimento suave e seguro. O roupeiro inclui um kit de pés e está disponível nas cores Jatobá/Grafite, Jatobá e Jatobá/Areia.\r\nMedidas: Altura 2,14m, Largura 2,04m, Profundidade 0,49m.', 1399.00, '/img/product_images/roupeiro_maringa.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(38, 'Roupeiro Frizatto', 'Roupeiro Frizzato combina modernidade e praticidade em um design elegante. Com três gavetas equipadas com corrediças telescópicas, oferece fácil acesso e organização para suas roupas. As portas possuem um detalhe frizado, trazendo um toque sofisticado, e são feitas de alumínio, garantindo durabilidade. Os cabideiros são também em alumínio, e os perfis antiempeno em aço adicionam resistência ao móvel. O roupeiro inclui um kit de pés e está disponível nas cores Freijó/off white/Titanium e Cinamomo/off white.\r\nMedidas: Altura 2,30m, Largura 1,60m, Profundidade 0,54m.', 1799.00, '/img/product_images/roupeiro_frizatto.jpg', 'quarto', 'guarda-roupas', 1, 1, NULL, NULL, NULL, 0),
(39, 'Roupeiro Ipê', 'Roupeiro Ipê é uma peça que une charme e funcionalidade, ideal para quem busca um toque moderno na decoração. Composto por duas gavetas equipadas com corrediças telescópicas, oferece amplo espaço de armazenamento. Os puxadores em ABS com acabamento metalizado dourado adicionam um detalhe sofisticado. O roupeiro conta com um aplique que remete à palha indiana, trazendo um charme especial. Suas portas possuem o sistema de dobradiças slow motion, proporcionando um fechamento suave e delicado. O cabideiro é feito em alumínio, garantindo resistência. Disponível nas cores Cinamomo/Branco e Cinamomo/Salvia.\r\nMedidas: Altura 1,95m, Largura 1,20m, Profundidade 0,47m.', 1399.00, '/img/product_images/roupeiro_ipe.jpg', 'quarto', 'guarda-roupas', 1, 1, NULL, NULL, NULL, 0),
(40, 'Roupeiro Style 240', 'Roupeiro Style 240 é a combinação ideal de amplo espaço e design contemporâneo. Fabricado com 100% MDF, oferece durabilidade e resistência. Este modelo possui gavetas com corrediças telescópicas, garantindo um acesso prático e eficiente. As gavetas são acompanhadas de distanciadores em MDF, que auxiliam na organização interna. O roupeiro inclui um kit de pés, complementando o visual moderno. Disponível nas cores Amêndoa clean e Amêndoa clean/off.\r\nMedidas: Altura 2,30m, Largura 2,40m, Profundidade 0,54m.', 2599.00, '/img/product_images/roupeiro_style_240.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(41, 'Roupeiro para Cama Solteiro Cancún', 'Roupeiro para Cama Box c/ Baú Solteiro Cancún é uma solução inteligente e funcional para quartos compactos. Fabricado em MDP, possui cabideiros metálicos que garantem resistência. Com gavetas equipadas com corrediças metálicas, oferece organização e praticidade. Os puxadores em PVC acrescentam um toque moderno ao design. A opção de combinar cores é ideal para personalizar o espaço, estando disponível nas cores Branco/Lilás e Jequitibá/Off. O kit de pés é vendido separadamente, permitindo um acabamento que valoriza o visual do móvel.\r\nMedidas: Altura 2,00m, Largura 1,57m, Profundidade 0,44m.', 1199.00, '/img/product_images/roupeiro_cama_box_cancun.jpg', 'quarto', 'guarda-roupas', 0, 1, NULL, NULL, NULL, 0),
(42, 'Mesa Orgânica Lizz + Cadeiras Vitta', 'A Mesa Orgânica Liz, acompanhada de 06 cadeiras Vita, é a escolha perfeita para quem busca sofisticação e conforto no ambiente de jantar. Com um tampo chanfrado em MDF de 25mm, revestido em vidro, e uma base de 25mm com detalhes curvos em dourado e pintura PU, esta mesa combina elegância e durabilidade. As cadeiras, disponíveis em tecido Terracota/boucle pérola ou Linho bege, possuem pés em metal dourado, garantindo um visual harmônico e moderno.\r\n\r\nMedidas da mesa:\r\n\r\nAltura: 0,80m\r\nLargura: 2,18m\r\nProfundidade: 1,08m\r\nPeso: 82,50kg\r\n\r\nMedidas da cadeira:\r\n\r\nAltura: 0,87m\r\nLargura: 0,58m\r\nProfundidade: 0,60m', 8999.00, '/img/product_images/mesa_organica_lizz_1.jpg', 'sala_de_jantar', 'mesas_de_jantar', 1, 0, '/img/product_images/mesa_organica_lizz_2.jpg', NULL, NULL, 0),
(44, 'Roupeiro Innova em vidro Reflect c/ LEDs', 'O Roupeiro Innova 6 Portas Reflect é a escolha perfeita para quem busca organização e estilo no quarto. Com um design sofisticado em Off White, este roupeiro possui iluminação LED que destaca seu interior, criando um ambiente aconchegante. As portas com vidro refletivo oferecem um toque moderno e elegante. Internamente, conta com corrediças telescópicas e um layout bem distribuído, garantindo praticidade na utilização do espaço e permitindo o armazenamento de diversas peças de vestuário.\r\n\r\nMedidas:\r\n\r\nAltura: 2,40m\r\nLargura: 2,50m\r\nProfundidade: 0,55m.', 6799.00, '/img/product_images/roupeiro_innova_em_vidro_1.jpg', 'quarto', 'guarda-roupas', 1, 0, '/img/product_images/roupeiro_innova_c_vidro_4.jpg', '/img/product_images/roupeiro_innova_em_vidro_3.jpg', NULL, 0),
(45, 'Roupeiro Panamá 3 Portas', 'Roupeiro Panamá 3 portas é uma excelente opção para quem busca elegância e funcionalidade no quarto. Produzido em MDP/MDF, possui corrediças telescópicas que garantem suavidade e resistência no uso das gavetas. Conta com 4 gavetas internas, maleiro, reforço duplo e espelho central repartido que amplia e valoriza o ambiente. O modelo está disponível nas cores Cinamomo/Off White, Branco, Cinamomo flex/Off White.\nMedidas: Altura 2,08m, Largura 1,85m, Profundidade 0,47m.', 1399.00, '/img/product_images/roupeiro_panama_1.jpg', 'quarto', 'guarda-roupas', 1, 0, '/img/product_images/roupeiro_panama_2.jpg', '/img/product_images/roupeiro_panama_3.jpg', '', 0),
(46, 'Roupeiro Bem Te Vi', 'Roupeiro Bem Te Vi é ideal para quem busca organização e praticidade, sem abrir mão do design. Produzido totalmente em MDF, possui 10 portas, sendo 2 delas com detalhes frisados que dão um charme especial ao móvel. Conta ainda com 3 gavetas amplas e resistentes, além de puxadores em MDF que reforçam a qualidade do produto. Disponível na elegante combinação Cinamomo/Off White e Cinamomo.\r\nMedidas: Altura 2,25m, Largura 1,85m, Profundidade 0,47m.\r\n\r\n', 1599.00, '/img/product_images/roupeiro_bem_te_vi_1.jpg', 'quarto', 'guarda-roupas', 0, 0, '/img/product_images/roupeiro_bem_te_vi_2.jpg', NULL, NULL, 0),
(47, 'Roupeiro Búzios', 'Roupeiro Búzios é a escolha ideal para quem busca funcionalidade com um design sofisticado. Produzido totalmente em MDF, conta com 2 portas de correr, sendo uma com espelho inteiro, agregando praticidade e amplitude ao ambiente. Internamente, oferece excelente espaço com 2 gavetas e corrediças telescópicas, garantindo suavidade no uso diário. Disponível nas combinações Nature/Off White, Branco e Nature, que traz aconchego e elegância ao quarto.\r\nMedidas: Altura 2,26m, Largura 1,61m, Profundidade 0,54m.', 1989.00, '/img/product_images/roupeiro_buzios_1.jpg', 'quarto', 'guarda-roupas', 0, 0, '/img/product_images/roupeiro_buzios_2.jpg', '/img/product_images/roupeiro_buzios_3.jpg', NULL, 0),
